package Tema2.Ejercicios;

public class for3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Desarrollar un programa que muestre la tabla de multiplicar del 5 (del 5 al
		// 50)

		for (int i = 0; i <= 10; i++) { // Desde i=0 hasta i=10, y vamos multiplicando por 5 el contador
			System.out.println("5 x " + i + " = " + (i * 5));

		}

	}

}
